function To = estperioautoco(x,fe)  % x=signal échantillonner  à étudier   %fréquence d’échantillonage%calcul de l’autocorrélation   Rxx=xcorr(x);  M=length(Rxx);  N=M+1;  N=N*0.5;%on regarde que d’un coté  Wxx=Rxx(N:M-1);  K=length(Wxx);  D=zeros(1,K-1);%recherche premier max local  for i=1:K-1     D(i)=Wxx(i)-Wxx(i+1);  end  L=1;  j=1;    while(L>=0 |(L<=0&& D(j)<0))      L=D(j)*D(j+1);            j=j+1;  end     plot([0:K-1],Wxx);  To= (j-1)*fe;